import logo from './logo.svg';
import './App.css';


class App extends Component{
  constructor(){

    super();

    this.state = {
      monsters : [ 
        {
          name: 'Abhinav',
          id: 'a1'
        },
        {
          name: 'Gaurav',
          id : 'a2'
        },
        {
          name: 'Mohit',
          id: 'a3'
        }
    ]
  };
}

  render() {

      return (
        <div className="App">
          {
            this.state.monsters.map(monster => (
              <h1 key={monster.id}> {monster.name
            }</h1>))}

        </div>
      );
    }
  }



export default App;
